mapper.select:
select *
from member



        and member_id = #{memberId}



        and user_id = #{userId}


;

mapper.update:
UPDATE MEMBER
SET USER_NM = #{userNm}, USER_PWD = #{userPwd}, USER_CHK_PWD = #{userChkPwd}, USER_ROLES = #{userRoles}, NICK_NM = #{nickNm}, EMAIL = #{email}
WHERE MEMBER_ID = #{memberId};
